import React, { Component } from "react";
import "./AddItem.css";

class AddItems extends Component {

  state = {
    name
  }

  render() {
    return (
      <div>
        <form>
          <input type="text" placeholder="Enter Text" id="name" onChange={this.handleChange} />
          <input type='submit' value='Add' />
        </form>
      </div>
    );
  }
}

export default AddItems;
